// █▀█████▀  ▀█████▀  ▀████████▀██▄█████████████▀██▄ █▀█████▀  ██████▀
//
// ===================================================================
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./interfaces/IAuthorization.sol";
import "./interfaces/IWETH.sol";

error ZeroNumber();
error UnsupportedCurrency(address currency);
error BalanceInsufficient();
error MaxWithdrawBalanceExceeded(uint256 expected, uint256 actual);
error WithdrawFailed();
error NoSyncRole();
error InvalidOffset();

contract LagomApplication is Ownable, AccessControl, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant DEFAULT_SYNC_ROLE = keccak256("DEFAULT_SYNC_ROLE");

    constructor(address _authorization, uint256 _networkId, address _weth) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(DEFAULT_SYNC_ROLE, _msgSender());
        _setRoleAdmin(DEFAULT_SYNC_ROLE, DEFAULT_ADMIN_ROLE);

        AUTHORIZATION_CONTRACT = IAuthorization(_authorization);
        WETH = IWETH(_weth);

        CEX_NETWORK_ID = _networkId;
    }

    IAuthorization public AUTHORIZATION_CONTRACT;
    IWETH public WETH;

    uint256 public CEX_NETWORK_ID;

    mapping(address => bool) private _acceptedCurrenciesOf;

    uint256 public currentLiquidityOffset;
    mapping (address => mapping (address => uint256)) availableLiquidities;

    struct WithdrawalParams {
        address currency;
        uint256 availableBalance;
        uint256 withdrawAmount;
        bytes32 commitHash;
        uint128 requestId;
        bytes   signature;
    }
    struct LiquiditySyncParam {
        address addr;
        address currency;
        uint256 share;
    }

    event SetCurrentLiquidityOffset(address indexed caller, uint256 offset);
    event Deposited(address indexed user, address indexed currency, uint256 amount);
    event Withdrawal(address indexed user, address indexed currency, uint128 withdrawId, uint256 amount);
    event Commitment(address indexed user, address indexed currency, uint256 balance, bytes32 commitHash);
    event AddLiquidityETH(address indexed owner, address indexed currency, uint256 amount);
    event EmergencyWithdraw(address indexed owner, address indexed currency, uint256 amount);

    /**
     * @notice Set WETH address
     *
     * @dev External function to change WETH address. Only admin can call this function
     * @param _weth new WETH
     */
    function setWETH(address _weth) external onlyOwner {
        WETH = IWETH(_weth);
    }

    /**
     * @notice Set CEX Network ID
     *
     * @dev External function to change CEX_NETWORK_ID. Only admin can call this function
     * @param _newNetworkId New CEX network id
     */
    function setCexNetworkId(uint256 _newNetworkId) external onlyOwner {
        CEX_NETWORK_ID = _newNetworkId;
    }

    /**
     * @notice Add Currencies
     *
     * @dev External function to add new currencies. Only admin can call this function.
     * @param _currencies list supported currencies
     */
    function addCurrencies(address[] calldata _currencies) external onlyOwner {
        for (uint256 i = 0; i < _currencies.length; i++) {
            address currency = _currencies[i];
            _acceptedCurrenciesOf[currency] = true;
        }
    }

    /**
     * @notice Remove Currencies
     *
     * @dev External function to remove currencies. Only admin can call this function.
     * @param _currencies list supported currencies
     */
    function removeCurrencies(address[] calldata _currencies) external onlyOwner {
        for (uint256 i = 0; i < _currencies.length; i++) {
            address currency = _currencies[i];
            _acceptedCurrenciesOf[currency] = false;
        }
    }

    /**
     * @notice Deposit
     * 
     * @dev External function to deposit coins to system. Anyone can call this function.
     * @param _currency coin address
     * @param _amount amount to deposit
     */
    function deposit(address _currency, uint256 _amount) external payable nonReentrant whenNotPaused {
        if (_amount == 0) {
            revert ZeroNumber();
        }
        if(!_acceptedCurrenciesOf[_currency]) {
            revert UnsupportedCurrency(_currency);
        }

        address outputCurrency = _currency;
        if (_currency == address(0)) {
            if (msg.value != _amount) {
                revert BalanceInsufficient();
            }
        } else {
            if(IERC20(_currency).balanceOf(_msgSender()) < _amount) {
                revert BalanceInsufficient();
            }
            IERC20(_currency).safeTransferFrom(_msgSender(), address(this), _amount);
            if (_currency == address(WETH)) {
                outputCurrency = address(0);
                WETH.withdraw(_amount);
            }
        }

        emit Deposited(_msgSender(), outputCurrency, _amount);
    }

    /**
     * @notice Withdraw
     * 
     * @dev External function to withdraw coins to their wallet. Anyone with valid signature can call this function.
     * @param params withdraw parameters
     */
    function withdraw(WithdrawalParams calldata params) external nonReentrant whenNotPaused {
        if (params.withdrawAmount == 0) {
            revert ZeroNumber();
        }
        if(!_acceptedCurrenciesOf[params.currency]) {
            revert UnsupportedCurrency(params.currency);
        }
        if (params.availableBalance < params.withdrawAmount) {
            revert MaxWithdrawBalanceExceeded(params.availableBalance, params.withdrawAmount);
        }

        // create signature
        bytes32 message = keccak256(
            abi.encodePacked(
                _msgSender(),
                "withdraw",
                CEX_NETWORK_ID,
                params.currency,
                params.availableBalance,
                params.withdrawAmount,
                params.commitHash,
                params.requestId
            )
        );
        // authorize request
        AUTHORIZATION_CONTRACT.authorize(message, params.requestId, params.signature);

        emit Commitment(_msgSender(), params.currency, params.availableBalance, params.commitHash);

        if (params.currency == address(0)) {
            if(address(this).balance < params.withdrawAmount) {
                revert BalanceInsufficient();
            }
            (bool success, ) = payable(_msgSender()).call{ value: params.withdrawAmount }("");
            if (!success) {
                revert WithdrawFailed();
            }
        } else {
            if(IERC20(params.currency).balanceOf(address(this)) < params.withdrawAmount) {
                revert BalanceInsufficient();
            }
            IERC20(params.currency).safeTransfer(_msgSender(), params.withdrawAmount);
        }

        emit Withdrawal(_msgSender(), params.currency, params.requestId, params.withdrawAmount);
    }

    /**
     * Sync available balances and withdrawals
     * @param params sync params
     */
    function syncLiquidity(uint256 offset, LiquiditySyncParam[] calldata params) external {
        if(!hasRole(DEFAULT_SYNC_ROLE, _msgSender())) {
            revert NoSyncRole();
        }
        if(currentLiquidityOffset > offset) {
            revert InvalidOffset();
        }

        for (uint256 i = 0; i < params.length; i++) {
            availableLiquidities[params[i].addr][params[i].currency] = params[i].share;
        }
        currentLiquidityOffset = offset;
    }

    function setLiquidityCurrentOffset(uint256 offset) external onlyOwner {
        currentLiquidityOffset = offset;
        emit SetCurrentLiquidityOffset(_msgSender(), offset);
    }

    function addLiquidityETH() external payable onlyOwner {
        if (msg.value == 0) {
            revert ZeroNumber();
        }

        emit AddLiquidityETH(_msgSender(), address(0), msg.value);
    }

    /**
     * @dev Emergency Withdraw
     *
     * @notice 
     */
    function emergencyWithdraw(address _currency) external onlyOwner {
        uint256 withdrawAmount = 0;
        if (_currency == address(0)) {
            withdrawAmount = address(this).balance;
            if (withdrawAmount == 0) {
                revert BalanceInsufficient();
            }
            (bool success, ) = payable(_msgSender()).call{ value: withdrawAmount }("");
            if (!success) {
                revert WithdrawFailed();
            }
        } else {
            withdrawAmount = IERC20(_currency).balanceOf(address(this));
            if(withdrawAmount == 0) {
                revert BalanceInsufficient();
            }
            IERC20(_currency).safeTransfer(_msgSender(), withdrawAmount);
        }

        emit EmergencyWithdraw(_msgSender(), _currency, withdrawAmount);
    }

    function pause() external onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() external onlyOwner whenPaused {
        _unpause();
    }

    /**
     * Fallback function to receive ETH
     */
    fallback() payable external {
        require(msg.data.length == 0);
    }

    receive() external payable {
        assert(msg.sender == address(WETH)); // only accept ETH via fallback from the WETH contract
    }
}","keccak256":"0x003a7b0b1d225d6eb7fdabcca1263c9d3b589b873555ca7c07044da3e43e404d","license":"MIT"},"contracts/interfaces/IAuthorization.sol":{"content":"// SPDX-License-Identifier: MIT
