interface IAuthorization {
    function authorize(
        bytes32 _message,
        uint128 _requestId,
        bytes memory _signature
    ) external;

    function verify(bytes32 _message, bytes memory _signature) external;
}","keccak256":"0x240a4fec1bf7fa033995079aa16945cfe30f7a0235265be33eafb9f154ff760a","license":"MIT"},"contracts/interfaces/IWETH.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IWETH {
    function deposit() external payable;
    function transfer(address to, uint value) external returns (bool);
    function withdraw(uint) external;
}","keccak256":"0x72e50b850c8d1bc2f069054aa224d26033544594467c829fcd454891c288b23c","license":"MIT"}},"version":1}
